import React from "react";

import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";

const NewExpense = (props) => {

  const saveExpenseDataHandler = (CopyExpenseDataFromForm) => {
    const expenseDataFromNewExpense = {
      ...CopyExpenseDataFromForm,
      id: Math.random().toString(),
    };
    props.onAddExpenseData(expenseDataFromNewExpense);
  };

  // let form = <ExpenseForm onSaveExpenseData={saveExpenseDataHandler}/>;
  const showForm = () => {
    return form = <ExpenseForm onSaveExpenseData={saveExpenseDataHandler}/>;
  }
  let form = <button onClick={showForm}>Add new Expence</button>

  return (
    <div className="new-expense">
      {form}
    </div>
  );
};

export default NewExpense;
