import React, { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [selectedTitle, setSelectedTitle] = useState("");
  const [selectedAmount, setSelectedAmount] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  // const [showForm, setShowForm] = useState();
  // const [hideForm, setHideForm] = useState();
  const changeTitleHandler = (event) => {
    setSelectedTitle(event.target.value);
  };
  const changeAmountHandler = (event) => {
    setSelectedAmount(event.target.value);
  };
  const changeDateHandler = (event) => {
    setSelectedDate(event.target.value);
  };
  const addFormHandler = (event) => {
    event.preventDefault();
    const expenseDataFromForm = {
      title: selectedTitle,
      amount: selectedAmount,
      date: new Date(selectedDate),
    };
    props.onSaveExpenseData(expenseDataFromForm); // wywoładnie dla rodzica
    setSelectedTitle("");
    setSelectedAmount("");
    setSelectedDate("");
  };

  const hideForm = (event) => {
    event.preventDefault();
    setFormContent(<button onClick={showForm}>Add new Expence</button>);
  };

  let form = (
    <section className="new-expense__controls">
      <div className="new-expense__control">
        <label htmlFor="">Title</label>
        <input
          type="text"
          value={selectedTitle}
          onChange={changeTitleHandler}
        />
      </div>
      <div className="new-expense__control">
        <label htmlFor="">Amount</label>
        <input
          type="number"
          min="0.01"
          step="0.01"
          value={selectedAmount}
          onChange={changeAmountHandler}
        />
      </div>
      <div className="new-expense__control">
        <label htmlFor="">Date</label>
        <input
          type="date"
          min="2019-01-01"
          max="2022-12-31"
          value={selectedDate}
          onChange={changeDateHandler}
        />
      </div>
      <div className="new-expense__actions">
        <button type="subbmit">Add new Expence</button>
        <button onClick={hideForm}>Cancel</button>
      </div>
    </section>
  );

  return <form action="" onSubmit={addFormHandler}>{form}</form>;
};
export default ExpenseForm;
