import React from "react";

import Chart from "../chart/Chart";

const ChartData = (props) => {
  const dataForChart = [
    {
      label: "Jan",
      value: 0,
    },
    {
      label: "Feb",
      value: 0,
    },
    {
      label: "Mar",
      value: 0,
    },
    {
      label: "Apr",
      value: 0,
    },
    {
      label: "May",
      value: 0,
    },
    {
      label: "Jun",
      value: 0,
    },
    {
      label: "July",
      value: 0,
    },
    {
      label: "Aug",
      value: 0,
    },
    {
      label: "Sep",
      value: 0,
    },
    {
      label: "Oct",
      value: 0,
    },
    {
      label: "Nov",
      value: 0,
    },
    {
      label: "Dec",
      value: 0,
    },
  ];

  for (const expense of props.filteredItems) {
    const expenseMonth = expense.date.getMonth();
    dataForChart[expenseMonth].value += expense.amount;
  }

  return <Chart data={dataForChart} />;
};

export default ChartData;
