import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import ExpenseCard from "../UI/ExpenseCard";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = ({ items, items2 }) => {
  const [selectedYear, setSelectedYear] = useState("2021");
  const [x, setX] = useState(items2);

  const changeSelectedYearHandler = (selectedYearFromFilter) => {
    console.log(`expenses: ${selectedYearFromFilter}`);
    setSelectedYear(selectedYearFromFilter);

    const isThisYear = (element) => {
      element = selectedYear;
    };

    let filtered = items2.filter(function (element) {
      return element.date === selectedYear;
    });
    console.log(filtered);
  };

  return (
    <div>
      <ExpenseCard className="expenses">
        <ExpensesFilter
          selected={selectedYear}
          onChangeSelectedYear={changeSelectedYearHandler}
        />
        {items.map((element) => (
          <ExpenseItem
            key={element.id}
            title={element.title}
            date={element.date}
            amount={element.amount}
          />
        ))}
      </ExpenseCard>
    </div>
  );
};

export default Expenses;
