import React from "react";

import "./Chart.css";
import ChartBar from "./ChartBar";



const Chart = (props) => {

    const dataValues = props.data.map(element => element.value);
    const totalMax = Math.max(...dataValues);
  return (
    <div className="chart">
      {props.data.map((element) => (
        <ChartBar
          key={element.label}
          value={element.value}
          maxValue= {totalMax}
          label={element.label}
        />
      ))}
    </div>
  );
};

export default Chart;
